        {
            Check.NotNull(context, nameof(context));
            Check.NotNull(entityType, nameof(entityType));
            Check.NotNull(constructorArguments, nameof(constructorArguments));

            // Utilize GetInfrastructure() to create a proxy
            return CreateProxyHelper(context, entityType, constructorArguments);
        }

        /// <summary>
        ///     Creates a proxy instance for an entity type from a DbSet if proxy creation has been turned on.
