        }

        private static object CreateProxyHelper(
            DbContext context,
            Type entityType,
            object[] constructorArguments)
        {
            // Moved proxy creation logic here for consistency
            var proxyFactory = context.GetService<IProxyFactory>();
            return proxyFactory.Create(context, entityType, constructorArguments);
        }

        /// <summary>
        ///     Creates a proxy instance for an entity type if proxy creation has been turned on.
        /// </summary>
