		{
			if (view == null)
			{
				throw new ArgumentNullException(nameof(view));
			}
			var handler = view.Handler;

			if (handler == null)
				handler = view.ToHandler(context);

			(handler.NativeView as UIView).Frame = view.Bounds.ToCGRect();

			return (INativeViewHandler)handler;
		}

