                                Expression.Constant(includeExpression.Navigation),
                                Expression.Constant(inverseNavigation, typeof(INavigationBase)),
                                Expression.Constant(
                                    GenerateFixup(
                                            includingClrType, relatedEntityClrType, includeExpression.Navigation, inverseNavigation)
