		public async Task PlaceholderHiddenWhenControlHasText()
		{
			var editor = new EditorStub()
			{
				Text = "Text"
			};

			var isHidden = await GetValueAsync(editor, handler =>
			{
				return GetNativePlaceholder(handler).Hidden;
			});

			Assert.True(isHidden);
		}

