		public int GetVisibleItemIndex(int x, int y)
		{
			int index = 0;
			if (x < 0 || y < 0)
				return index;
			if (_scrollCanvasSize.Width < x || _scrollCanvasSize.Height < y)
				return CollectionView.Count - 1;

			int first = 0;
			if (_accumulatedItemSizes == null)
			{
